mapper.select:
SELECT B.BOARD_SEQ, B.BOARD_TYPE, B.TITLE, B.CONTENTS, B.REG_DATE FROM T_BOARD B
AND B.TITLE LIKE CONCAT('%', #{keyword}, '%')
AND B.BOARD_TYPE IN (
#{value}
)
ORDER BY B.REG_DATE DESC

mapper.update:
UPDATE T_BOARD
SET
	BOARD_TYPE = #{boardType.code},
	TITLE = #{title},
	CONTENTS = #{contents}
WHERE BOARD_SEQ = #{boardSeq}
